import React, { useState, useContext } from 'react'

import Card from '../../shared/components/UIElements/Card';
import Input from '../../shared/components/FormElements/Input';
import Button from '../../shared/components/FormElements/Button'
import {VALIDATOR_EMAIL, VALIDATOR_MINLENGTH, VALIDATOR_REQUIRE} from '../../shared/util/validators';
import { useForm } from '../../shared/hooks/form-hook';
import { AuthContext } from '../../shared/context/auth-context';
import ErrorModal from '../../shared/components/UIElements/ErrorModal';
import LoadingSpinner from '../../shared/components/UIElements/LoadingSpinner';
import './Auth.css'

const Auth = () => {
    const auth = useContext(AuthContext);

    const [isLoginMode, setIsLoginMode] = useState(true);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError]  = useState(false);

    const [formState, inputHandler, setFormData] = useForm({
        email: {
            value: '',
            isValid: false
        },
        password: {
            value: '',
            isValid: false
        }
    }, false);

    const switchModeHandler = () => {
        if(!isLoginMode){
            setFormData({
                ...formState.inputs,
                name: undefined
            }, formState.inputs.email.isValid && formState.inputs.password.isValid);
        }
        else {
            setFormData({
                ...formState.inputs,
                name : {
                    value: '',
                    isValid: false
                }
            }, false)
        }
        setIsLoginMode(prevMode => !prevMode);
    };

    const authSubmiteHandler = async event => {
        event.preventDefault();

        setIsLoading(true);

        if(isLoginMode){
            try {
                const response = await fetch('http://localhost:5000/api/users/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    nume: formState.inputs.name.value,
                    email: formState.inputs.email.value,
                    password: formState.inputs.password.value
                    })
                });

                const responseData = await response.json();
                if(!response.ok){
                    throw new Error(responseData.message);
                }
                setIsLoading(false);       
                auth.login();
            } catch (err){
                setIsLoading(false);
                setError(err.message || 'A aparut o eroare. Incearca din nou');
            }
        } else {

            try {
                const response = await fetch('http://localhost:5000/api/users/signup', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    name: formState.inputs.name.value,
                    email: formState.inputs.email.value,
                    password: formState.inputs.password.value
                    })
                });

                const responseData = await response.json();
                if(!response.ok){
                    throw new Error(responseData.message);
                }
                setIsLoading(false);       
                auth.login();
            } catch (err){
                setIsLoading(false);
                setError(err.message || 'A aparut o eroare. Incearca din nou');
            }
        }
    };

    const errorHandler = () => {
        setError(null);
    };

    return ( <React.Fragment>
        <ErrorModal error={error} onClear={errorHandler}/>
        <Card className="authentication">
        {isLoading && <LoadingSpinner asOverlay/>}
        <h2>Login required</h2>
        <hr />
        <form onSubmit={authSubmiteHandler}>
            {!isLoginMode && (
                <Input
                    element="input"
                    id="name"
                    type="text"
                    label="Username"
                    validators={[VALIDATOR_REQUIRE()]}
                    errorText="Trebuie sa alegi un nume de utilizator."
                    onInput={inputHandler}/>
            )}
            <Input 
                element="input" 
                id="email" 
                type="email"
                label="E-Mail" 
                validators={[VALIDATOR_EMAIL()]}
                errorText="Trebuie sa introduci o adresa de email valida."
                onInput={inputHandler}/>
            <Input 
                element="input" 
                id="password" 
                type="password"
                label="Password" 
                validators={[VALIDATOR_MINLENGTH(5)]}
                errorText="Parola trebuie sa aiba minim 5 caractere."
                onInput={inputHandler}/>
            <Button type="submit" disabled={!formState.isValid}>
                {isLoginMode ? 'LOGIN' : 'SIGNUP'}
            </Button>
            </form>
            <Button danger onClick={switchModeHandler}>
                SWITCH TO {isLoginMode ? 'SIGNUP' : 'LOGIN'}
            </Button>
        </Card>
        </React.Fragment> );
};

export default Auth;
